import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import MyButton from '../../util/MyButton';
import dayjs from 'dayjs';
import {Link} from 'react-router-dom';
import Comments from './Comments'
import CommentForm from './CommentForm';

import { Slide } from 'react-slideshow-image';

//MUI stuff
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import CircularProgress from '@material-ui/core/CircularProgress';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import LikeButton from './LikeButton';
//Icons
import CloseIcon from '@material-ui/icons/Close';
import Edit from '@material-ui/icons/Edit';
import ChatIcon from '@material-ui/icons/Chat';

//redux
import {connect} from 'react-redux';
import {getScream, clearErrors} from '../../redux/actions/dataActions';

const styles = {
    closeButton: {
        position: 'absolute',
        left : '90%'
    },
    editButton: {
        position: 'absolute',
        left: '84%',
        top: '10%',
    },
    dialogContent: {
        padding: 20
    },
    spinnerDiv: {
        textAlign: 'center',
        marginTop: 50,
        marginBottom: 50,
    },
}

class EditScream extends Component {

    state = {
        open: false,
        oldPath: '',
        newPath: '',
        name: '',
        body: '',
        price: '',
        busyDates: [],
        errors: {}
    };

    componentDidMount(){
        if(this.props.openDialog){
            this.handleOpen();
        }
    }

    handleOpen = () => {
        let oldPath = window.location.pathname;

        const {username, screamId} = this.props;
        let newPath = `/users/${username}/scream/${screamId}`;

        if(oldPath === newPath){
            oldPath = `/users/${username}`;
        }

        window.history.pushState(null, null, newPath);

        this.setState({open:true, oldPath, newPath});
        this.props.getScream(this.props.screamId);
        this.setState({
            name: this.props.name,
            body: this.props.body, 
            price: this.props.price, 
            busyDates: this.props.busyDates});
    }

    handleClose = () => {
        window.history.pushState(null, null, this.state.oldPath);
        this.setState({open:false});
        this.props.clearErrors();
    }



    render() {
        const {errors} = this.state;
        const {classes, scream : {photos, name, category, price, screamId, body, createdAt, likeCount, commentCount, userImage, username, comments},
         UI: {loading}} = this.props;

         const dialogMarkup = loading ? (
             <div className={classes.spinnerDiv}>
                <CircularProgress size={150} thickness={2}/>
             </div>
         ) : (
            <form onSubmit={this.handleSubmit}>
                <TextField name="name" type="text" label="Edit Name" placeholder="Edit Name" error={errors.name ? true : false}
                helperText={errors.name} className={classes.textField} onChange={this.handleChange} fullWidth/>
                <TextField name="body" type="text" label="Edit Body" multiline rows="3" placeholder="Edit Body" error={errors.body ? true : false}
                helperText={errors.body} className={classes.textField} onChange={this.handleChange} fullWidth/>
                <TextField name="price" type="text" label="Edit Price" placeholder="€" error={errors.price ? true : false}
                helperText={errors.price} className={classes.textField} onChange={this.handleChange}/>
                <br/>
                <div className={classes.divText}>
                <span>Choose the dates that you are not available:</span>
                </div>
                <br/>
                <DayPicker className={classes.dayPicker} disabledDays={{ before: new Date() }} selectedDays={this.state.busyDates} onDayClick={this.handleDayClick}/>
                <br/>
                <ImageUploader withPreview={true} withIcon={true} buttonText='Choose images' onChange={this.onDrop} label="Upload maximum 5 images" imgExtension={['.jpg', '.png']} maxFileSize={5242880}/>
                <Button type="submit" variant="contained" color="primary" className={classes.submitButton} disabled={loading}>
                Submit
                {loading && (
                    <CircularProgress size={30} className={classes.progressSpinner}/>
                )}
                </Button>
            </form>
         )


         return (
            <Fragment>
                 <MyButton onClick={this.handleOpen} tip="Edit Post" tipClassName={classes.editButton}>
                     <Edit color="primary"/>
                 </MyButton>
                 <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <MyButton tip="Close" onClick={this.handleClose} tipClassName={classes.closeButton}>
                        <CloseIcon/>
                    </MyButton>
                    <DialogContent className={classes.dialogContent}>
                        {dialogMarkup}
                    </DialogContent>
                 </Dialog>
             </Fragment>
        )
    }
}

EditScream.propTypes = {
    clearErrors: PropTypes.func.isRequired,
    getScream: PropTypes.func.isRequired,
    screamId: PropTypes.string.isRequired,
    username: PropTypes.string.isRequired,
}



const mapStateToProps = (state) => ({
    scream: state.data.scream,
    UI: state.UI
});

const mapActionsToProps = {
    getScream,
    clearErrors,
};

export default connect(mapStateToProps, mapActionsToProps)(withStyles(styles)(EditScream));
