import React, { Component, Fragment } from 'react'
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import dayjs from 'dayjs';
import MyButton from '../../util/MyButton';
import Grid from '@material-ui/core/Grid';


//Mui Stuff
import Button from '@material-ui/core/Button';
import EmojiObjectsIcon from '@material-ui/icons/EmojiObjects';
import CloseIcon from '@material-ui/icons/Close';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import CircularProgress from '@material-ui/core/CircularProgress';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';


//MUI Pickers
import DateFnsUtils from '@date-io/date-fns';
import {MuiPickersUtilsProvider, KeyboardDatePicker} from '@material-ui/pickers';


import {connect} from 'react-redux';
// import {bookScream} from '../../redux/actions/dataActions';

const styles = {
    suggestButton: {
        marginTop: '5px',
    },
    dialogContent: {
        padding: 20
    },
    closeButton: {
        position: 'absolute',
        left : '90%'
    },
}

class SuggestScreamDialog extends Component {

    state = {
        open: false,
        date: new Date(),
        price: '',
        location: true,
        music: false,
        photo: false,
        entertainment: false,
        others: false,
    };

    handleOpen = () => {
        this.setState({open: true})
    }

    handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    handleDateChange = (date) => {
        this.setState({date: date});
    }
    
    handleClose = () => {
        this.setState({open: false})
    }

    render() {

        const {classes, UI: {loading}} = this.props;
        const dialogMarkup = loading ? (
            <div className={classes.spinnerDiv}>
               <CircularProgress size={150} thickness={2}/>
            </div>
        ) : (
            <Grid container spacing={2}>
               <Grid item xs={4}>
                    <MuiPickersUtilsProvider name="date" utils={DateFnsUtils}>
                        <KeyboardDatePicker 
                        disablePast
                        name="date"
                        margin="normal"
                        format="MM/dd/yyyy"
                        value={this.state.date}
                        onChange={this.handleDateChange}
                        />
                    </MuiPickersUtilsProvider>
                    </Grid>
                    <Grid item xs={4}>
                        <TextField variant="outlined" name="price" type="text" label="Add a price" placeholder="Maximum Price" onChange={this.handleChange}/>
                    </Grid>
                    <Grid item xs={4}>
                    <FormControl component="fieldset" className={classes.formControl}>
                        <FormLabel component="legend">Choose categories</FormLabel>
                        <FormGroup>
                        <FormControlLabel
                            control={<Checkbox checked={this.state.location} onChange={this.handleChange} name="location" />}
                            label="Location / EventHall"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.music} onChange={this.handleChange} name="music" />}
                            label="Music"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.photo} onChange={this.handleChange} name="photo" />}
                            label="Photo & Video"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.entertainment} onChange={this.handleChange} name="entertainment" />}
                            label="Entertainment"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.others} onChange={this.handleChange} name="others" />}
                            label="Others"
                        />
                        </FormGroup>
                    </FormControl>
                    </Grid>
            </Grid>
        );
        return (
            <Fragment>
                <Button variant="contained" color="primary" size="large" onClick={this.handleOpen} className={classes.suggestButton} startIcon={<EmojiObjectsIcon />}>
                    Suggest me a service!
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <MyButton tip="Close" onClick={this.handleClose} tipClassName={classes.closeButton}>
                        <CloseIcon/>
                    </MyButton>
                    <DialogContent className={classes.dialogContent}>
                        {dialogMarkup}
                    </DialogContent>
                </Dialog>
            </Fragment>
        )
    }
}

SuggestScreamDialog.propTypes = {
    classes: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired
}

const mapStateToProps = state =>({
    // user: state.user
    UI: state.UI
})

export default connect(mapStateToProps)(withStyles(styles)(SuggestScreamDialog));
