import React, { Component, Fragment } from 'react'
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import dayjs from 'dayjs';


//Mui Stuff
import Button from '@material-ui/core/Button';
import EmojiObjectsIcon from '@material-ui/icons/EmojiObjects';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogActions from '@material-ui/core/DialogActions';

import {connect} from 'react-redux';
// import {bookScream} from '../../redux/actions/dataActions';

const styles = {
    bookButton: {
        marginLeft: 20,
    },
}

class SuggestScreamDialog extends Component {

    state = {
        open: false
    };

    handleOpen = () => {
        this.setState({open: true})
    }
    
    handleClose = () => {
        this.setState({open: false})
    }

    render() {

        const {classes} = this.props;
        return (
            <Fragment>
                <Button variant="contained" color="primary" size="large" onClick={this.handleOpen} className={classes.button} startIcon={<EmojiObjectsIcon />}>
                    Suggest me a service!
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    {/* <DialogTitle>
                        Are you sure you want to book this service in {dayjs(new Date(date)).format('DD MMMM YYYY')} for {price}â‚¬?
                    </DialogTitle>
                    <DialogActions>
                        <Button onClick={this.handleClose} color="secondary">
                            Cancel
                        </Button>
                        <Button onClick={this.bookScreamOpen} color="primary">
                            Book
                        </Button>
                    </DialogActions> */}
                </Dialog>
            </Fragment>
        )
    }
}

SuggestScreamDialog.propTypes = {
    classes: PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
    // user: state.user
})

export default connect(mapStateToProps)(withStyles(styles)(SuggestScreamDialog));
