import React, { Component } from 'react'
import Grid from '@material-ui/core/Grid';
import Profile from '../components/profile/Profile';
import PropTypes from 'prop-types';
import ScreamSkeleton from '../util/ScreamSkeleton';

import {connect} from 'react-redux';
import {getBookings} from '../redux/actions/userActions';


class bookings extends Component {

    componentWillReceiveProps(nextProps){
        if (nextProps !== this.props){
            console.log(this.props);
            console.log(nextProps);
            const {credentials: {username}} = nextProps.user;
            console.log(username);
            nextProps.getBookings(username);
        }
        // this.props.getBookings("user");
        // const {credentials: {username}} = this.props.user;
        // console.log(username);
        // if (username)
        //     this.props.getBookings(username);
    }

    render() {
        const {bookings, loading} = this.props.user;
        let recentBookingsMarkup = !loading ? (bookings && bookings.length !== 0 ? (
        bookings.map(booking => null)) : (<h2>No Bookings Yet!</h2>)
        ) : <ScreamSkeleton/>
        return (
            <div>      
                <Grid container spacing={4}>
                    <Grid item sm={8} xs={12}>
                        {recentBookingsMarkup}
                    </Grid>
                    <Grid item sm={4} xs={12}>
                        <Profile/>
                    </Grid>
                </Grid>
            </div>
        )
  }
}

bookings.propTypes = {
    getBookings: PropTypes.func.isRequired,
    // data: PropTypes.object.isRequired,
    user: PropTypes.object.isRequired
}

const mapToStateProps = state => ({
    user: state.user,
    // data: state.data
})

export default connect(mapToStateProps, {getBookings})(bookings);
