import React, { Component, Fragment } from 'react'
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';
import dayjs from 'dayjs';
import MyButton from '../../util/MyButton';
import Grid from '@material-ui/core/Grid';


//Mui Stuff
import Button from '@material-ui/core/Button';
import EmojiObjectsIcon from '@material-ui/icons/EmojiObjects';
import CloseIcon from '@material-ui/icons/Close';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import CircularProgress from '@material-ui/core/CircularProgress';
import FormControl from '@material-ui/core/FormControl';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import FormHelperText from '@material-ui/core/FormHelperText';


//select
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';


//MUI Pickers
import DateFnsUtils from '@date-io/date-fns';
import {MuiPickersUtilsProvider, KeyboardDatePicker} from '@material-ui/pickers';


import {connect} from 'react-redux';
// import {bookScream} from '../../redux/actions/dataActions';

const styles = {
    suggestButton: {
        marginTop: '5px',
    },
    dialogContent: {
        padding: 20
    },
    closeButton: {
        position: 'absolute',
        left : '92%'
    },
    formControlSuggestion: {
        marginTop: 20
    }
}

class SuggestScreamDialog extends Component {

    state = {
        open: false,
        date: new Date(),
        price: '',
        location: true,
        music: false,
        photo: false,
        entertainment: false,
        others: false,
        suggestionType: 'suggested'
    };

    handleOpen = () => {
        this.setState({open: true})
    }

    handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    handleDateChange = (date) => {
        this.setState({date: date});
    }
    
    handleClose = () => {
        this.setState({open: false})
    }

    handleCheckedChange = (event) => {
        this.setState({[event.target.name]: event.target.checked});
    }

    render() {

        const {classes, UI: {loading}} = this.props;
        const error = [this.state.location, this.state.music, this.state.photo, this.state.entertainment, this.state.others].filter((v) => v).length < 1;
        const dialogMarkup = loading ? (
            <div className={classes.spinnerDiv}>
               <CircularProgress size={150} thickness={2}/>
            </div>
        ) : (
            <Grid container spacing={2}>
               <Grid item xs={4}>
                    <MuiPickersUtilsProvider name="date" utils={DateFnsUtils}>
                        <KeyboardDatePicker 
                        disablePast
                        name="date"
                        margin="normal"
                        format="MM/dd/yyyy"
                        value={this.state.date}
                        onChange={this.handleDateChange}
                        />
                    </MuiPickersUtilsProvider>
                    </Grid>
                    <Grid item xs={4}>
                        <TextField variant="outlined" name="price" type="text" label="Add a price" placeholder="Maximum Price" onChange={this.handleChange}/>

                        <FormControl name="suggestionType" className={classes.formControlSuggestion} fullWidth>
                                <InputLabel>Suggestion Type</InputLabel>
                                <Select name="suggestionType" value={this.state.suggestionType} onChange={this.handleChange}>
                                    <MenuItem value={'low'}>Lowest Price</MenuItem>
                                    <MenuItem value={'suggested'}>Suggested</MenuItem>
                                    <MenuItem value={'high'}>Higher Price</MenuItem>
                                </Select>
                            </FormControl>
                    </Grid>
                    <Grid item xs={3}>
                    <FormControl required error={error} component="fieldset" className={classes.formControl}>
                        {/* <FormLabel component="legend">Choose categories</FormLabel> */}
                        <FormGroup>
                        <FormControlLabel
                            control={<Checkbox checked={this.state.location} onChange={this.handleCheckedChange} name="location" color="primary" />}
                            label="Location / EventHall"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.music} onChange={this.handleCheckedChange} name="music" color="primary"/>}
                            label="Music"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.photo} onChange={this.handleCheckedChange} name="photo" color="primary"/>}
                            label="Photo & Video"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.entertainment} onChange={this.handleCheckedChange} name="entertainment" color="primary" />}
                            label="Entertainment"
                        />
                        <FormControlLabel
                            control={<Checkbox checked={this.state.others} onChange={this.handleCheckedChange} name="others" color="primary" />}
                            label="Others"
                        />
                        </FormGroup>
                        {error && (<FormHelperText>Choose at least one!</FormHelperText>)}
                    </FormControl>
                    </Grid>
            </Grid>
        );
        return (
            <Fragment>
                <Button variant="contained" color="primary" size="large" onClick={this.handleOpen} className={classes.suggestButton} startIcon={<EmojiObjectsIcon />}>
                    Suggest me a service!
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <MyButton tip="Close" onClick={this.handleClose} tipClassName={classes.closeButton}>
                        <CloseIcon/>
                    </MyButton>
                    <DialogContent className={classes.dialogContent}>
                        {dialogMarkup}
                    </DialogContent>
                </Dialog>
            </Fragment>
        )
    }
}

SuggestScreamDialog.propTypes = {
    classes: PropTypes.object.isRequired,
    UI: PropTypes.object.isRequired
}

const mapStateToProps = state =>({
    // user: state.user
    UI: state.UI
})

export default connect(mapStateToProps)(withStyles(styles)(SuggestScreamDialog));
