import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import MyButton from '../../util/MyButton';

import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import CircularProgress from '@material-ui/core/CircularProgress';
import AddIcon from '@material-ui/icons/Add';
import CloseIcon from '@material-ui/icons/Close';

//Select
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';


//Redux
import {connect} from 'react-redux';
import {postScream, clearErrors} from '../../redux/actions/dataActions';

const styles = {
    textField: {
        margin: '10px auto 10px auto',
    },
    formControl: {
        margin: '10px 10px 10px auto',
        minWidth: 120,

    },
    submitButton: {
        position: 'relative',
        float: 'right',
        marginTop: 10,
        marginBottom: 10,
    },
    progressSpinner: {
        position: 'absolute'
    },
    closeButton: {
        position: 'absolute',
        left: '91%',
        top: '3%'
    },
}

class PostScream extends Component{
    state = {
        open: false,
        name: '',
        body: '',
        category: '',
        price: '',
        busyDates: [],
        photos: [],
        errors: {}
    }

    componentWillReceiveProps(nextProps){
        if(nextProps.UI.errors){
            this.setState({
                errors: nextProps.UI.errors
            });
        }
        if(!nextProps.UI.errors && !nextProps.UI.loading){
            this.setState({body: '', open:false, errors: {}});
        }
    }

    handleOpen = () => {
        this.setState({open:true});
    }
    handleClose = () => {
        this.props.clearErrors();
        this.setState({open:false, errors: {}});
    }

    handleChange = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.props.postScream({body: this.state.body});
    }

    render(){
        const {errors} = this.state;
        const {classes, UI: {loading}} = this.props;
        return (
            <Fragment>
                <MyButton onClick={this.handleOpen} tip="New Post">
                    <AddIcon/>
                </MyButton>
                <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <MyButton tip="Close" onClick={this.handleClose} tipClassName={classes.closeButton}>
                        <CloseIcon/>
                    </MyButton>
                    <DialogTitle>
                        New post
                    </DialogTitle>
                    <DialogContent>
                        <form onSubmit={this.handleSubmit}>
                            <TextField name="name" type="text" label="Add a name" placeholder="Post Name" error={errors.body ? true : false}
                            helperText={errors.name} className={classes.textField} onChange={this.handleChange} fullWidth/>
                            <TextField name="body" type="text" label="Add a post" multiline rows="3" placeholder="New Post" error={errors.name ? true : false}
                            helperText={errors.body} className={classes.textField} onChange={this.handleChange} fullWidth/>
                            <FormControl className={classes.formControl}>
                                <InputLabel>Category</InputLabel>
                                <Select name="category" value={'Others'} onChange={this.handleChange}>
                                    <MenuItem value={'EventHall'}>Location / EventHall</MenuItem>
                                    <MenuItem value={'Music'}>Music</MenuItem>
                                    <MenuItem value={'Entertainment'}>Entertainment</MenuItem>
                                </Select>
                            </FormControl>
                            <TextField name="price" type="text" label="Price" placeholder="€" error={errors.price ? true : false}
                            helperText={errors.price} className={classes.textField} onChange={this.handleChange}/>
                         <Button type="submit" variant="contained" color="primary" className={classes.submitButton} disabled={loading}>
                             Submit
                             {loading && (
                                 <CircularProgress size={30} className={classes.progressSpinner}/>
                             )}
                         </Button>
                        </form>
                    </DialogContent>
                </Dialog>
            </Fragment>

        )
    }
}

PostScream.propTypes = {
    postScream: PropTypes.func.isRequired,
    UI:PropTypes.object.isRequired,
    clearErrors: PropTypes.func.isRequired
}

const mapStateToProps = (state) => ({
    UI: state.UI
})

export default connect(mapStateToProps, {postScream, clearErrors})(withStyles(styles)(PostScream));
