import React, { Component } from 'react'
import Grid from '@material-ui/core/Grid';
import Profile from '../components/profile/Profile';
import PropTypes from 'prop-types';
import ScreamSkeleton from '../util/ScreamSkeleton';
  



import {connect} from 'react-redux';
import {getBookings} from '../redux/actions/dataActions';

class bookings extends Component {

    componentDidMount(){
        const {user} = this.props;
        console.log(user.credentials.username);
        this.props.getBookings(user.credentials.username);
    }
    render() {
        const {bookings, loading} = this.props;
        let recentBookingsMarkup = !loading ? (bookings && bookings.length !== 0 ? (
        bookings.map(booking => null)) : (<h2>No Bookings Yet!</h2>)
        ) : <ScreamSkeleton/>
        return (
            <div>      
                <Grid container spacing={4}>
                    <Grid item sm={8} xs={12}>
                        {recentBookingsMarkup}
                    </Grid>
                    <Grid item sm={4} xs={12}>
                        <Profile/>
                    </Grid>
                </Grid>
            </div>
        )
  }
}

bookings.propTypes = {
    getBookings: PropTypes.func.isRequired,
    data: PropTypes.object.isRequired,
    user: PropTypes.object.isRequired
}

const mapToStateProps = state => ({
    user: state.user,
    data: state.data
})

export default connect(mapToStateProps, {getBookings})(bookings);
