import React, { Component, Fragment } from 'react'
import withStyles from '@material-ui/core/styles/withStyles';
import PropTypes from 'prop-types';

//Mui Stuff
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogTitle from '@material-ui/core/DialogTitle';
import DialogActions from '@material-ui/core/DialogActions';

import {connect} from 'react-redux';
import {deleteScream} from '../../redux/actions/dataActions';

const styles = {
    bookButton: {
        marginLeft: 20,
    },
}

class BookScream extends Component {

    state = {
        open: false
    };

    handleOpen = () => {
        this.setState({open: true})
    }
    
    handleClose = () => {
        this.setState({open: false})
    }

    bookScream = () => {
        // this.props.deleteScream(this.props.screamId);
        this.setState({open: false});
    }

    render() {

        const {classes, date, price} = this.props;
        return (
            <Fragment>
                <Button variant="contained" color="primary" onClick={this.handleOpen} className={classes.bookButton}> 
                BOOK IT
                </Button>
                <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <DialogTitle>
                        Are you sure you want to book this service in {date} for {price}â‚¬?
                    </DialogTitle>
                    <DialogActions>
                        <Button onClick={this.handleClose} color="secondary">
                            Cancel
                        </Button>
                        <Button onClick={this.bookScream} color="primary">
                            Book
                        </Button>
                    </DialogActions>
                </Dialog>
            </Fragment>
        )
    }
}

BookScream.propTypes = {
    // deleteScream: PropTypes.func.isRequired,
    screamId: PropTypes.string.isRequired,
    date: PropTypes.string.isRequired,
    price: PropTypes.string.isRequired,
    classes: PropTypes.object.isRequired,
}

export default connect(null, {})(withStyles(styles)(BookScream));
