import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types';
import withStyles from '@material-ui/core/styles/withStyles';
import MyButton from '../../util/MyButton';
import dayjs from 'dayjs';
import {Link} from 'react-router-dom';
import Comments from './Comments'
import CommentForm from './CommentForm';

import { Slide } from 'react-slideshow-image';

//MUI stuff
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';
import CircularProgress from '@material-ui/core/CircularProgress';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import LikeButton from './LikeButton';
//Icons
import CloseIcon from '@material-ui/icons/Close';
import Edit from '@material-ui/icons/Edit';
import ChatIcon from '@material-ui/icons/Chat';

//redux
import {connect} from 'react-redux';
import {getScream, clearErrors} from '../../redux/actions/dataActions';

const styles = {
    closeButton: {
        position: 'absolute',
        left : '90%'
    },
    expandButton: {
        position: 'absolute',
        left: '90%'
    },
    dialogContent: {
        padding: 20
    },
}

class EditScream extends Component {

    state = {
        open: false,
        oldPath: '',
        newPath: '',
        name: '',
        body: '',
        price: '',
        busyDates: [],
        errors: {}
    };

    componentDidMount(){
        if(this.props.openDialog){
            this.handleOpen();
        }
    }

    handleOpen = () => {
        let oldPath = window.location.pathname;

        const {username, screamId} = this.props;
        let newPath = `/users/${username}/scream/${screamId}`;

        if(oldPath === newPath){
            oldPath = `/users/${username}`;
        }

        window.history.pushState(null, null, newPath);

        this.setState({open:true, oldPath, newPath});
        // this.props.getScream(this.props.screamId);
    }

    handleClose = () => {
        window.history.pushState(null, null, this.state.oldPath);
        this.setState({open:false});
        // this.props.clearErrors();
    }



    render() {
        const {classes, scream : {photos, name, category, price, screamId, body, createdAt, likeCount, commentCount, userImage, username, comments},
         UI: {loading}} = this.props;

         const dialogMarkup = loading ? (
             <div className={classes.spinnerDiv}>
                <CircularProgress size={150} thickness={2}/>
             </div>
         ) : (
             <Grid container spacing={4}>
                 <Grid item sm={5}>
                     <img src={userImage} alt="Profile" className={classes.profileImage}/>
                 </Grid>
                 <Grid item sm={7}>
                    <Typography component={Link} color="primary" variant="h5" to={`/users/${username}`}>
                        {username}
                    </Typography>
                    <Typography variant="h6">{name}</Typography>
                    <Typography variant="body2" color="textSecondary">
                        {dayjs(createdAt).format('h:mm a, MMMM DD YYYY')}
                    </Typography>
                    <hr className={classes.invisibleSeparator}/>
                    <Typography variant="body1">
                        {body}
                    </Typography>
                    <Typography variant="body1">Category: {category}</Typography>
                    <Typography variant="h6">Price: {price} â‚¬</Typography>
                    <LikeButton screamId={screamId}/>
                    <span>{likeCount} Likes</span>
                    <MyButton tip="Comments">
                        <ChatIcon color="primary"/>
                    </MyButton>
                    <span>{commentCount} Comments</span>
                 </Grid>
                 {photos && (
                     <Slide className={classes.slide} {...properties}>
                         {photos.map((photo) => {return(
                            <div key={photo} className="each-slide">
                                <img className={classes.photosPost} src={photo} alt="Presentation"/>
                            </div>
                         )})}
                    </Slide>
                 )}
                 <hr className={classes.visibleSeparator}/>
                 <CommentForm screamId={screamId}/>
                 <Comments comments={comments}/>
             </Grid>
         )


         return (
            <Fragment>
                 <MyButton onClick={this.handleOpen} tip="Edit Post" tipClassName={classes.editButton}>
                     <Edit color="primary"/>
                 </MyButton>
                 <Dialog open={this.state.open} onClose={this.handleClose} fullWidth maxWidth="sm">
                    <MyButton tip="Close" onClick={this.handleClose} tipClassName={classes.closeButton}>
                        <CloseIcon/>
                    </MyButton>
                    <DialogContent className={classes.dialogContent}>
                        {dialogMarkup}
                    </DialogContent>
                 </Dialog>
             </Fragment>
        )
    }
}

